import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    categories: {
      1: 'home',
      2: 'work',
      3: 'education',
      4: 'hobby',
      5: 'frends'
    },
    keys: Object.keys(localStorage),
    notes: []
  },
  getters: {
    getCategories (state) {
      return state.categories;
    },
    getKeys (state) {
      return state.keys;
    },
    getNotes (state) {
      return state.notes;
    }
  },
  mutations: {
  },
  actions: {
    getNotesFomLS (context) {
      for (let key of context.state.keys) {
        context.state.notes[key] = localStorage.getItem(key);
      }
    }
  },
  modules: {
  }
})
