import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    categories: {
      1: 'home',
      2: 'work',
      3: 'education',
      4: 'hobby',
      5: 'frends'
    },
    notes: localStorage.getItem(0)
  },
  getters: {
    getCategories (state) {
      return state.categories;
    },
    getNotes (state) {
      return state.notes;
    }
  },
  mutations: {
  },
  actions: {
  },
  modules: {
  }
})
